mapper.update:
UPDATE profiles
SET following_count = following_count + 1
WHERE user_id = #{userId}

UPDATE profiles
SET followers_count = followers_count + 1
WHERE user_id = #{userId}

UPDATE profiles
SET following_count = following_count - 1
WHERE user_id = #{userId}

UPDATE profiles
SET followers_count = followers_count - 1
WHERE user_id = #{userId}
